    if (bothNumbers(a, b)) {                                                                       \
        auto tmp = (int64_t)numValue(a) op(int64_t) numValue(b);                                   \
        if ((int)tmp == tmp)                                                                       \
            return TAG_NUMBER((int)tmp);                                                           \
