    return ((uintptr_t)v >> 48) != 0;
#else
    (void)v;
    return false;
#endif
}

inline bool isPointer(TValue v) {
    return !isDouble(v) && v != 0 && ((intptr_t)v & 3) == 0;
}

inline bool isTagged(TValue v) {
    return (!isDouble(v) && ((intptr_t)v & 3)) || !v;
