            BoxedString *right;
        } cons;
        struct {
            uint16_t size;   // in bytes
            uint16_t length; // in characters
            uint16_t *list;
        } skip;
        struct {
            uint16_t size;   // in bytes
            uint16_t length; // in characters
            uint16_t list[0];
        } skip_pack;
#endif
